            startDate,
            endDate
          );
        } else if (platform === "flipkart-grocery") {
          parsedData = parseFlipkartSecondaryData(req.file.buffer, periodType, businessUnit, startDate, endDate);
        }

        if (!parsedData) {
          return res.status(400).json({ error: "Unsupported platform" });
