client/src/components/po/line-item-row.t.content:
import { useState, useEffect, useRef } from "react";
import { useQuery } from "@tanstack/react-query";
import { Trash2, Search } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useDebounce } from "@/hooks/use-debounce";
import type { PfItemMst, SapItemMst, PfMst, InsertPfOrderItems } from "@shared/schema";

interface LineItem extends InsertPfOrderItems {
  tempId: string;
}

interface LineItemRowProps {
  item: LineItem;
  platformId?: number;
  onUpdate: (updates: Partial<LineItem>) => void;
  onRemove: () => void;
}

interface PlatformItemWithDetails extends PfItemMst {
  sapItem: SapItemMst;
  platform: PfMst;
}

const itemStatusOptions = [
  { value: "Pending", label: "Pending" },
  { value: "Invoiced", label: "Invoiced" },
  { value: "Dispatched", label: "Dispatched" },
  { value: "Delivered", label: "Delivered" },
  { value: "Stock Issue", label: "Stock Issue" },
  { value: "Cancelled", label: "Cancelled" },
  { value: "Expired", label: "Expired" },
  { value: "Price Difference", label: "Price Difference" },
  { value: "MOV Issue", label: "MOV Issue" },
  { value: "Hold", label: "Hold" },
  { value: "CN", label: "CN" },
  { value: "RTV", label: "RTV" },
  { value: "Case Pack Issue", label: "Case Pack Issue" }
];

export function LineItemRow({ item, platformId, onUpdate, onRemove }: LineItemRowProps) {
  const [searchTerm, setSearchTerm] = useState(item.item_name || "");
  const [showDropdown, setShowDropdown] = useState(false);
  const [isSearching, setIsSearching] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Debounce the search term to avoid too many API calls
  const debouncedSearchTerm = useDebounce(searchTerm, 300);

  // Fetch platform items for search
  const { data: platformItems = [], isLoading } = useQuery<PlatformItemWithDetails[]>({
    queryKey: ["/api/platform-items", platformId, debouncedSearchTerm],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (platformId) params.append('platformId', platformId.toString());
      if (debouncedSearchTerm) params.append('search', debouncedSearchTerm);

      const response = await fetch(`/api/platform-items?${params.toString()}`);
      if (!response.ok) {
        throw new Error('Failed to fetch platform items');
      }
      return response.json();
    },
    enabled: !!platformId && debouncedSearchTerm.length > 0 && isSearching
  });

  // Handle clicking outside of dropdown
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        dropdownRef.current && 
        !dropdownRef.current.contains(event.target as Node) &&
        inputRef.current &&
        !inputRef.current.contains(event.target as Node)
      ) {
        setShowDropdown(false);
        setIsSearching(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Calculate landing rate when basic rate or GST rate changes
  useEffect(() => {
    const basicRate = parseFloat(item.basic_rate || "0");
    const gstRate = parseFloat(item.gst_rate || "0");
    const landingRate = basicRate + (basicRate * gstRate / 100);

    if (landingRate !== parseFloat(item.landing_rate || "0")) {
      onUpdate({ landing_rate: landingRate.toFixed(2) });
    }
  }, [item.basic_rate, item.gst_rate, item.landing_rate, onUpdate]);

  const handleItemSelect = (selectedItem: PlatformItemWithDetails) => {
    onUpdate({
      item_name: selectedItem.pf_itemname,
      sap_code: selectedItem.sapItem.itemcode,
      category: selectedItem.sapItem.itemgroup || "",
      subcategory: selectedItem.sapItem.subgroup || "",
      gst_rate: selectedItem.sapItem.taxrate?.toString() || "0"
    });
    setSearchTerm(selectedItem.pf_itemname);
    setShowDropdown(false);
    setIsSearching(false);
  };

  const handleInputChange = (field: keyof LineItem, value: string | number) => {
    onUpdate({ [field]: value });
  };

  const handleSearchInputChange = (value: string) => {
    setSearchTerm(value);
    onUpdate({ item_name: value });
    setIsSearching(true);
    setShowDropdown(value.length > 0);
  };

  return (
    <tr className="hover:bg-gray-50 dark:hover:bg-gray-900/50 transition-colors">
      <td className="px-4 py-4">
        <div className="relative w-full">
          <div className="relative">
            <Input
              ref={inputRef}
              value={searchTerm}
              onChange={(e) => handleSearchInputChange(e.target.value)}
              placeholder="Search and select item..."
              onFocus={() => {
                if (searchTerm.length > 0) {
                  setIsSearching(true);
                  setShowDropdown(true);
                }
              }}
              className="w-full pr-8 text-sm"
            />
            {isSearching && (
              <Search className="absolute right-2 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            )}
          </div>

          {showDropdown && (
            <div 
              ref={dropdownRef}
              className="absolute z-50 w-full mt-1 bg-white border border-gray-200 rounded-lg shadow-xl max-h-60 overflow-y-auto"
            >
              {isLoading ? (
                <div className="px-4 py-3 text-center text-gray-500">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-primary mx-auto"></div>
                  <p className="mt-2 text-sm">Searching...</p>
                </div>
              ) : platformItems.length > 0 ? (
                platformItems.map((platformItem) => (
                  <div
                    key={platformItem.id}
                    className="px-4 py-3 hover:bg-blue-50 cursor-pointer border-b border-gray-100 last:border-b-0 transition-colors"
                    onClick={() => handleItemSelect(platformItem)}
                  >
                    <div className="font-medium text-gray-900">{platformItem.pf_itemname}</div>
                    <div className="text-sm text-gray-600 mt-1">
                      <span className="font-medium">SAP:</span> {platformItem.sapItem.itemcode} | 
                      <span className="ml-2">{platformItem.sapItem.itemgroup}</span>
                      {platformItem.sapItem.taxrate && (
                        <span className="ml-2 text-green-600">GST: {platformItem.sapItem.taxrate}%</span>
                      )}
                    </div>
                  </div>
                ))
              ) : debouncedSearchTerm.length > 0 ? (
                <div className="px-4 py-3 text-center text-gray-500">
                  No items found for "{debouncedSearchTerm}"
                </div>
              ) : null}
            </div>
          )}
        </div>
      </td>

      <td className="px-4 py-4">
        <Input 
          value={item.sap_code || ""} 
          readOnly 
          className="w-full bg-gray-50 text-sm font-mono"
        />
      </td>

      <td className="px-4 py-4">
        <Input
          value={item.category || ""}
          onChange={(e) => handleInputChange("category", e.target.value)}
          placeholder="Category"
          readOnly
          className="w-full bg-gray-50 text-sm"
        />
      </td>

      <td className="px-4 py-4">
        <Input
          type="number"
          min="1"
          value={item.quantity || ""}
          onChange={(e) => handleInputChange("quantity", parseInt(e.target.value) || 0)}
          placeholder="Qty"
          className="w-full text-sm text-center"
        />
      </td>

      <td className="px-4 py-4">
        <Input
          type="number"
          step="0.01"
          value={item.basic_rate || ""}
          onChange={(e) => handleInputChange("basic_rate", e.target.value)}
          placeholder="0.00"
          className="w-full text-sm text-right"
        />
      </td>

      <td className="px-4 py-4">
        <Input
          type="number"
          step="0.01"
          value={item.gst_rate || ""}
          onChange={(e) => handleInputChange("gst_rate", e.target.value)}
          placeholder="18"
          className="w-full text-sm text-center"
        />
      </td>

      <td className="px-4 py-4">
        <Input
          type="number"
          step="0.01"
          value={item.landing_rate || ""}
          readOnly
          className="w-full bg-gray-50 text-sm text-right font-medium text-green-600"
        />
      </td>

      <td className="px-4 py-4">
        <div className="flex items-center justify-center space-x-1">
          <Select
            value={item.status || "Pending"}
            onValueChange={(value) => handleInputChange("status", value)}
          >
            <SelectTrigger className="w-24 text-xs">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              {itemStatusOptions.map((option) => (
                <SelectItem key={option.value} value={option.value}>
                  {option.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>

          <Button
            variant="ghost"
            size="sm"
            onClick={onRemove}
            className="text-red-600 hover:text-red-800 hover:bg-red-50 p-1 h-8 w-8"
          >
            <Trash2 size={14} />
          </Button>
        </div>
      </td>
    </tr>
  );
}

client/src/components/po/platform-po-for.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { z } from "zod";
import { Plus, Check, CalendarDays } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DatePicker } from "@/components/ui/date-picker";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { LineItemRow } from "./line-item-row";
import { SeedButton } from "@/components/seed-button";
import type { PfMst, DistributorMst, InsertPfOrderItems } from "@shared/schema";

const poFormSchema = z.object({
  po_number: z.string().min(1, "PO number is required"),
  platform: z.number().min(1, "Platform selection is required"),
  status: z.string().min(1, "Status is required"),
  order_date: z.string().min(1, "Order date is required"),
  expiry_date: z.string().optional(),
  appointment_date: z.string().optional(),
  region: z.string().min(1, "Region is required"),
  state: z.string().min(1, "State is required"),
  city: z.string().min(1, "City is required"),
  area: z.string().optional(),
  serving_distributor: z.string().optional(),
  attachment: z.string().optional()
});

type POFormData = z.infer<typeof poFormSchema>;

interface LineItem extends InsertPfOrderItems {
  tempId: string;
  po_id?: number;
}

const statusOptions = [
  { value: "Open", label: "Open" },
  { value: "Closed", label: "Closed" },
  { value: "Cancelled", label: "Cancelled" },
  { value: "Expired", label: "Expired" },
  { value: "Duplicate", label: "Duplicate" }
];

const regionOptions = [
  { value: "North India", label: "North India" },
  { value: "South India", label: "South India" },
  { value: "East India", label: "East India" },
  { value: "West India", label: "West India" }
];

const stateOptions = [
  { value: "Punjab", label: "Punjab" },
  { value: "Delhi", label: "Delhi" },
  { value: "Karnataka", label: "Karnataka" },
  { value: "Maharashtra", label: "Maharashtra" },
  { value: "West Bengal", label: "West Bengal" }
];

const cityOptions = [
  { value: "Chandigarh", label: "Chandigarh" },
  { value: "New Delhi", label: "New Delhi" },
  { value: "Bangalore", label: "Bangalore" },
  { value: "Mumbai", label: "Mumbai" },
  { value: "Kolkata", label: "Kolkata" }
];

export function PlatformPOForm() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [lineItems, setLineItems] = useState<LineItem[]>([]);

  const form = useForm<POFormData>({
    resolver: zodResolver(poFormSchema),
    defaultValues: {
      po_number: "",
      platform: 0,
      status: "Open",
      order_date: new Date().toISOString().split('T')[0],
      expiry_date: "",
      appointment_date: "",
      region: "",
      state: "",
      city: "",
      area: "",
      serving_distributor: "none",
      attachment: ""
    }
  });

  // Fetch platforms
  const { data: platforms = [] } = useQuery<PfMst[]>({
    queryKey: ["/api/platforms"]
  });

  // Fetch distributors
  const { data: distributors = [] } = useQuery<DistributorMst[]>({
    queryKey: ["/api/distributors"]
  });

  // Create PO mutation
  const createPoMutation = useMutation({
    mutationFn: async (data: { po: POFormData; items: InsertPfOrderItems[] }) => {
      const response = await apiRequest("POST", "/api/pos", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Purchase order created successfully!"
      });
      form.reset();
      setLineItems([]);
      queryClient.invalidateQueries({ queryKey: ["/api/pos"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create purchase order",
        variant: "destructive"
      });
    }
  });

  const addLineItem = () => {
    const newItem: LineItem = {
      tempId: `temp-${Date.now()}`,
      po_id: 0, // Temporary value, will be set by backend
      item_name: "",
      quantity: 0,
      sap_code: "",
      category: "",
      subcategory: "",
      basic_rate: "0",
      gst_rate: "0",
      landing_rate: "0",
      status: "Pending"
    };
    setLineItems([...lineItems, newItem]);
  };

  const updateLineItem = (tempId: string, updates: Partial<LineItem>) => {
    setLineItems(items => 
      items.map(item => 
        item.tempId === tempId ? { ...item, ...updates } : item
      )
    );
  };

  const removeLineItem = (tempId: string) => {
    setLineItems(items => items.filter(item => item.tempId !== tempId));
  };

  const calculateTotals = () => {
    const totalQuantity = lineItems.reduce((sum, item) => sum + (item.quantity || 0), 0);
    const totalValue = lineItems.reduce((sum, item) => {
      const landingRate = parseFloat(item.landing_rate || "0");
      const quantity = item.quantity || 0;
      return sum + (landingRate * quantity);
    }, 0);

    return { totalQuantity, totalValue };
  };

  const onSubmit = (data: POFormData) => {
    if (lineItems.length === 0) {
      toast({
        title: "Error",
        description: "Please add at least one line item",
        variant: "destructive"
      });
      return;
    }

    const items: InsertPfOrderItems[] = lineItems.map(({ tempId, ...item }) => ({
      ...item,
      po_id: 0 // Will be set by the backend
    }));

    // Convert "none" back to null for database storage
    const processedData = {
      ...data,
      serving_distributor: data.serving_distributor === "none" ? null : data.serving_distributor
    };

    createPoMutation.mutate({ po: processedData, items });
  };

  const { totalQuantity, totalValue } = calculateTotals();

  return (
    <div className="max-w-6xl mx-auto space-y-6">
      {/* Form Header */}
      <Card className="shadow-lg border-0 bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-gray-900 dark:to-gray-800">
        <CardHeader className="border-b border-blue-100 dark:border-gray-700 bg-white/50 dark:bg-gray-800/50 backdrop-blur-sm">
          <div className="flex items-center justify-between">
            <CardTitle className="text-xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
              Create New Purchase Order
            </CardTitle>
            <div className="flex items-center space-x-3">
              <Button 
                type="submit" 
                form="po-form"
                disabled={createPoMutation.isPending}
                className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700"
              >
                <Check className="mr-2 h-4 w-4" />
                {createPoMutation.isPending ? 'Creating...' : 'Create PO'}
              </Button>
            </div>
          </div>
        </CardHeader>

        <CardContent className="p-8 bg-white/80 dark:bg-gray-900/80 backdrop-blur-sm">
          <Form {...form}>
            <form id="po-form" onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">

              {/* Basic Information Section */}
              <div className="space-y-6">
                <div className="flex items-center space-x-2 pb-2">
                  <div className="h-px bg-gradient-to-r from-blue-200 to-indigo-200 flex-1"></div>
                  <h4 className="text-sm font-medium text-blue-600 dark:text-blue-400 px-3">Basic Information</h4>
                  <div className="h-px bg-gradient-to-r from-indigo-200 to-blue-200 flex-1"></div>
                </div>
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                <FormField
                  control={form.control}
                  name="po_number"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>PO Number *</FormLabel>
                      <FormControl>
                        <Input placeholder="Enter PO number" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="platform"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Platform *</FormLabel>
                      <Select onValueChange={(value) => field.onChange(parseInt(value))} value={field.value?.toString()}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select Platform" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {platforms.map((platform) => (
                            <SelectItem key={platform.id} value={platform.id.toString()}>
                              {platform.pf_name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="status"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Status *</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select Status" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {statusOptions.map((option) => (
                            <SelectItem key={option.value} value={option.value}>
                              {option.label}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="order_date"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center gap-2">
                        <CalendarDays className="h-4 w-4" />
                        Order Date *
                      </FormLabel>
                      <FormControl>
                        <DatePicker
                          date={field.value ? new Date(field.value) : undefined}
                          onDateChange={(date) => field.onChange(date ? date.toISOString().split('T')[0] : '')}
                          placeholder="Select order date"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="expiry_date"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center gap-2">
                        <CalendarDays className="h-4 w-4" />
                        Expiry Date
                      </FormLabel>
                      <FormControl>
                        <DatePicker
                          date={field.value ? new Date(field.value) : undefined}
                          onDateChange={(date) => field.onChange(date ? date.toISOString().split('T')[0] : '')}
                          placeholder="Select expiry date"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="appointment_date"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center gap-2">
                        <CalendarDays className="h-4 w-4" />
                        Appointment Date
                      </FormLabel>
                      <FormControl>
                        <DatePicker
                          date={field.value ? new Date(field.value) : undefined}
                          onDateChange={(date) => field.onChange(date ? date.toISOString().split('T')[0] : '')}
                          placeholder="Select appointment date"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="region"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Region *</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select Region" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {regionOptions.map((option) => (
                            <SelectItem key={option.value} value={option.value}>
                              {option.label}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="state"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>State *</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select State" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {stateOptions.map((option) => (
                            <SelectItem key={option.value} value={option.value}>
                              {option.label}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="city"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>City *</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select City" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {cityOptions.map((option) => (
                            <SelectItem key={option.value} value={option.value}>
                              {option.label}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="area"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Area</FormLabel>
                      <FormControl>
                        <Input placeholder="Enter area/locality" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="serving_distributor"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Serving Distributor</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value || "none"}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select Distributor" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="none">-- No Distributor --</SelectItem>
                          {distributors.map((distributor) => (
                            <SelectItem key={distributor.id} value={distributor.distributor_name}>
                              {distributor.distributor_name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                </div>
              </div>
            </form>
          </Form>
        </CardContent>
      </Card>

      {/* Line Items Section */}
      <Card className="shadow-lg border-0">
        <CardHeader className="border-b border-blue-100 dark:border-gray-700 bg-gradient-to-r from-green-50 to-emerald-50 dark:from-gray-800 dark:to-gray-900">
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg font-bold bg-gradient-to-r from-green-600 to-emerald-600 bg-clip-text text-transparent">
              Order Items
            </CardTitle>
            <Button 
              type="button" 
              onClick={addLineItem} 
              className="bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 shadow-md"
            >
              <Plus className="mr-2 h-4 w-4" />
              Add Item
            </Button>
          </div>
        </CardHeader>

        {/* Items Table */}
        <div className="overflow-x-auto">
          <table className="w-full table-fixed min-w-[1200px]">
            <colgroup>
              <col className="w-80" />
              <col className="w-28" />
              <col className="w-32" />
              <col className="w-24" />
              <col className="w-28" />
              <col className="w-24" />
              <col className="w-28" />
              <col className="w-32" />
            </colgroup>
            <thead className="bg-gradient-to-r from-slate-50 to-gray-50 dark:from-gray-800 dark:to-gray-900">
              <tr>
                <th className="px-4 py-4 text-left text-xs font-semibold text-gray-700 dark:text-gray-300 uppercase tracking-wider">Item Details</th>
                <th className="px-4 py-4 text-left text-xs font-semibold text-gray-700 dark:text-gray-300 uppercase tracking-wider">SAP Code</th>
                <th className="px-4 py-4 text-left text-xs font-semibold text-gray-700 dark:text-gray-300 uppercase tracking-wider">Category</th>
                <th className="px-4 py-4 text-left text-xs font-semibold text-gray-700 dark:text-gray-300 uppercase tracking-wider">Quantity</th>
                <th className="px-4 py-4 text-left text-xs font-semibold text-gray-700 dark:text-gray-300 uppercase tracking-wider">Basic Rate</th>
                <th className="px-4 py-4 text-left text-xs font-semibold text-gray-700 dark:text-gray-300 uppercase tracking-wider">GST Rate</th>
                <th className="px-4 py-4 text-left text-xs font-semibold text-gray-700 dark:text-gray-300 uppercase tracking-wider">Landing Rate</th>
                <th className="px-4 py-4 text-left text-xs font-semibold text-gray-700 dark:text-gray-300 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white dark:bg-gray-950 divide-y divide-gray-100 dark:divide-gray-800">
              {lineItems.map((item) => (
                <LineItemRow
                  key={item.tempId}
                  item={item}
                  platformId={form.watch("platform")}
                  onUpdate={(updates) => updateLineItem(item.tempId, updates)}
                  onRemove={() => removeLineItem(item.tempId)}
                />
              ))}
              {lineItems.length === 0 && (
                <tr>
                  <td colSpan={8} className="px-6 py-8 text-center text-gray-500">
                    No items added yet. Click "Add Item" to get started.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        {/* Summary Section */}
        {lineItems.length > 0 && (
          <div className="px-6 py-6 border-t border-blue-100 dark:border-gray-700 bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-gray-800 dark:to-gray-900">
            <div className="flex justify-between items-center">
              <div className="flex items-center space-x-6 text-sm font-medium text-gray-700 dark:text-gray-300">
                <div className="flex items-center space-x-2">
                  <div className="h-2 w-2 bg-blue-500 rounded-full"></div>
                  <span>{lineItems.length} item(s)</span>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="h-2 w-2 bg-green-500 rounded-full"></div>
                  <span>Total Quantity: {totalQuantity}</span>
                </div>
              </div>
              <div className="text-xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
                Total Value: â‚¹{totalValue.toFixed(2)}
              </div>
            </div>
          </div>
        )}
      </Card>

      {/* Action Buttons */}
      <div className="mt-6 flex justify-end space-x-4">
        <Button 
          type="submit" 
          form="po-form"
          disabled={createPoMutation.isPending}
          className="px-8 font-medium"
        >
          Create Purchase Order
        </Button>
      </div>
    </div>
  );
}

