import { storage } from "./storage";
import type { InsertPfMst, InsertSapItemMst, InsertPfItemMst } from "@shared/schema";

export async function seedTestData() {
  try {
    // Get existing platforms
    const existingPlatforms = await storage.getAllPlatforms();

    // Find platforms we need
    const amazonPlatform = existingPlatforms.find(p => p.pf_name === "Amazon");
    const flipkartPlatform = existingPlatforms.find(p => p.pf_name === "Flipkart");
    const blinkitPlatform = existingPlatforms.find(p => p.pf_name === "Blinkit");

    if (!amazonPlatform || !flipkartPlatform || !blinkitPlatform) {
      console.error("Required platforms not found. Please ensure Amazon, Flipkart, and Blinkit platforms exist.");
      return { success: false, error: "Required platforms not found" };
    }

    const createdPlatforms = [amazonPlatform, flipkartPlatform, blinkitPlatform];

    // Create SAP items
    const sapItems: InsertSapItemMst[] = [
      { itemcode: "SAP001", itemname: "Laptop - Dell Inspiron 15", itemgroup: "Electronics", subgroup: "Computers", taxrate: "18" },
      { itemcode: "SAP002", itemname: "Mobile - Samsung Galaxy S21", itemgroup: "Electronics", subgroup: "Mobile Phones", taxrate: "18" },
      { itemcode: "SAP003", itemname: "Headphones - Sony WH-1000XM4", itemgroup: "Electronics", subgroup: "Audio", taxrate: "18" },
      { itemcode: "SAP004", itemname: "T-Shirt - Cotton Blue", itemgroup: "Apparel", subgroup: "Men's Clothing", taxrate: "5" },
      { itemcode: "SAP005", itemname: "Running Shoes - Nike Air Max", itemgroup: "Footwear", subgroup: "Sports Shoes", taxrate: "12" },
      { itemcode: "SAP006", itemname: "Coffee Maker - Nespresso", itemgroup: "Home Appliances", subgroup: "Kitchen", taxrate: "18" },
      { itemcode: "SAP007", itemname: "Yoga Mat - Premium", itemgroup: "Sports", subgroup: "Fitness", taxrate: "12" },
      { itemcode: "SAP008", itemname: "Book - Programming Guide", itemgroup: "Books", subgroup: "Technical", taxrate: "0" },
      { itemcode: "SAP009", itemname: "Watch - Casio Digital", itemgroup: "Accessories", subgroup: "Watches", taxrate: "18" },
      { itemcode: "SAP010", itemname: "Backpack - Travel 40L", itemgroup: "Bags", subgroup: "Travel", taxrate: "18" }
    ];

    const createdSapItems = [];
    for (const item of sapItems) {
      try {
        const created = await storage.createSapItem(item);
        createdSapItems.push(created);
      } catch (error: any) {
        // If item already exists, find and use the existing one
        if (error.code === '23505') {
          const existingSapItems = await storage.getAllSapItems();
          const existing = existingSapItems.find(existing => existing.itemcode === item.itemcode);
          if (existing) {
            createdSapItems.push(existing);
          }
        } else {
          throw error;
        }
      }
    }

    // Create platform items (linking platforms to SAP items)
    const platformItems: InsertPfItemMst[] = [
      // Amazon items
      { pf_id: createdPlatforms[0].id, sap_id: createdSapItems[0].id, pf_itemcode: "AMZ-LAP-001", pf_itemname: "Dell Inspiron 15 Laptop (8GB RAM, 512GB SSD)" },
      { pf_id: createdPlatforms[0].id, sap_id: createdSapItems[1].id, pf_itemcode: "AMZ-MOB-001", pf_itemname: "Samsung Galaxy S21 5G (128GB, Phantom Gray)" },
      { pf_id: createdPlatforms[0].id, sap_id: createdSapItems[2].id, pf_itemcode: "AMZ-AUD-001", pf_itemname: "Sony WH-1000XM4 Wireless Noise Cancelling Headphones" },
      { pf_id: createdPlatforms[0].id, sap_id: createdSapItems[5].id, pf_itemcode: "AMZ-APP-001", pf_itemname: "Nespresso Vertuo Coffee Machine" },

      // Flipkart items
      { pf_id: createdPlatforms[1].id, sap_id: createdSapItems[0].id, pf_itemcode: "FK-LAP-001", pf_itemname: "DELL Inspiron Core i5 11th Gen Laptop" },
      { pf_id: createdPlatforms[1].id, sap_id: createdSapItems[1].id, pf_itemcode: "FK-MOB-001", pf_itemname: "SAMSUNG Galaxy S21 5G (Phantom Gray, 128 GB)" },
      { pf_id: createdPlatforms[1].id, sap_id: createdSapItems[3].id, pf_itemcode: "FK-CLO-001", pf_itemname: "Men's Cotton T-Shirt (Blue, Size L)" },
      { pf_id: createdPlatforms[1].id, sap_id: createdSapItems[4].id, pf_itemcode: "FK-SHO-001", pf_itemname: "NIKE Air Max Running Shoes For Men" },

      // Blinkit items (was Myntra)
      { pf_id: createdPlatforms[2].id, sap_id: createdSapItems[6].id, pf_itemcode: "BLK-FOOD-001", pf_itemname: "Premium Yoga Mat - 6mm Thick" },
      { pf_id: createdPlatforms[2].id, sap_id: createdSapItems[7].id, pf_itemcode: "BLK-BOOK-001", pf_itemname: "Programming Guide - Learn Python" },
      { pf_id: createdPlatforms[2].id, sap_id: createdSapItems[3].id, pf_itemcode: "BLK-CLO-001", pf_itemname: "Men's Cotton T-Shirt - Blue XL" },
      { pf_id: createdPlatforms[2].id, sap_id: createdSapItems[4].id, pf_itemcode: "BLK-SHO-001", pf_itemname: "Nike Running Shoes - Size 10" }
    ];

    for (const item of platformItems) {
      await storage.createPlatformItem(item);
    }

    console.log("Test data seeded successfully!");
    return { success: true };
  } catch (error) {
    console.error("Error seeding test data:", error);
    return { success: false, error };
  }
}

client/src/pages/po-details.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { useParams, useLocation } from "wouter";
import { format } from "date-fns";
import { ArrowLeft, Edit, Trash2, Download, Mail, Phone, Calendar, MapPin, Package, DollarSign, Clock, Building } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { PfPo, PfMst, PfOrderItems } from "@shared/schema";

interface POWithDetails extends Omit<PfPo, 'platform'> {
  platform: PfMst;
  orderItems: PfOrderItems[];
}

export default function PODetails() {
  const params = useParams();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const poId = params.id;

  const { data: po, isLoading, error } = useQuery<POWithDetails>({
    queryKey: [`/api/pos/${poId}`],
    enabled: !!poId
  });

  const handleEdit = () => {
    setLocation(`/po-edit/${poId}`);
  };

  const handleDelete = async () => {
    if (!po) return;

    if (confirm(`Are you sure you want to delete PO ${po.po_number}?`)) {
      try {
        await apiRequest('DELETE', `/api/pos/${poId}`);
        toast({
          title: "Success",
          description: "Purchase order deleted successfully"
        });
        setLocation("/platform-po");
      } catch (error) {
        toast({
          title: "Error",
          description: "Failed to delete purchase order",
          variant: "destructive"
        });
      }
    }
  };

  const handleDownload = () => {
    if (!po) return;

    // Generate and download PO as PDF or CSV
    toast({
      title: "Download Started",
      description: `Downloading PO ${po.po_number}`
    });
  };

  const getStatusBadgeVariant = (status: string) => {
    switch (status.toLowerCase()) {
      case 'open': return 'default';
      case 'closed': return 'secondary';
      case 'cancelled': return 'destructive';
      case 'expired': return 'destructive';
      case 'duplicate': return 'outline';
      default: return 'default';
    }
  };

  const calculatePOTotals = (items: PfOrderItems[]) => {
    const totalQuantity = items.reduce((sum, item) => sum + item.quantity, 0);
    const totalValue = items.reduce((sum, item) => {
      return sum + (parseFloat(item.landing_rate) * item.quantity);
    }, 0);
    return { totalQuantity, totalValue };
  };

  if (isLoading) {
    return (
      <div className="flex-1 flex flex-col overflow-hidden">
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading purchase order details...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error || !po) {
    return (
      <div className="flex-1 flex flex-col overflow-hidden">
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-2">Purchase Order Not Found</h2>
            <p className="text-gray-600 mb-4">The requested purchase order could not be found.</p>
            <Button onClick={() => setLocation("/platform-po")}>
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back to Purchase Orders
            </Button>
          </div>
        </div>
      </div>
    );
  }

  const { totalQuantity, totalValue } = calculatePOTotals(po.orderItems);

  return (
    <div className="flex-1 flex flex-col overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-gray-900 dark:to-gray-800 shadow-lg border-b border-blue-100 dark:border-gray-700 px-6 py-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Button 
              variant="outline" 
              onClick={() => setLocation("/platform-po")}
              className="hover:bg-blue-50"
            >
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back
            </Button>
            <div>
              <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
                {po.po_number}
              </h1>
              <p className="text-gray-600 dark:text-gray-300 mt-1">
                {po.platform.pf_name} â€¢ Created {format(new Date(po.order_date), 'MMM dd, yyyy')}
              </p>
            </div>
            <Badge 
              variant={getStatusBadgeVariant(po.status)}
              className="px-3 py-1 text-sm font-semibold"
            >
              {po.status}
            </Badge>
          </div>
          <div className="flex items-center space-x-3">
            <Button variant="outline" onClick={handleDownload}>
              <Download className="mr-2 h-4 w-4" />
              Download
            </Button>
            <Button variant="outline" onClick={handleEdit}>
              <Edit className="mr-2 h-4 w-4" />
              Edit
            </Button>
            <Button variant="outline" onClick={handleDelete} className="text-red-600 hover:text-red-700 hover:bg-red-50">
              <Trash2 className="mr-2 h-4 w-4" />
              Delete
            </Button>
          </div>
        </div>
      </header>

      {/* Content */}
      <main className="flex-1 overflow-y-auto p-6 bg-gray-50 dark:bg-gray-900">
        <div className="max-w-7xl mx-auto space-y-6">
          {/* PO Overview */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <Card className="lg:col-span-2">
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Package className="mr-2 h-5 w-5" />
                  Purchase Order Details
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-1">
                    <p className="text-sm font-medium text-gray-500">Order Date</p>
                    <div className="flex items-center">
                      <Calendar className="mr-2 h-4 w-4 text-gray-400" />
                      <span className="font-semibold">{format(new Date(po.order_date), 'MMM dd, yyyy')}</span>
                    </div>
                  </div>
                  <div className="space-y-1">
                    <p className="text-sm font-medium text-gray-500">Expiry Date</p>
                    <div className="flex items-center">
                      <Clock className="mr-2 h-4 w-4 text-gray-400" />
                      <span className="font-semibold">
                        {po.expiry_date ? format(new Date(po.expiry_date), 'MMM dd, yyyy') : 'Not set'}
                      </span>
                    </div>
                  </div>
                  <div className="space-y-1">
                    <p className="text-sm font-medium text-gray-500">Location</p>
                    <div className="flex items-center">
                      <MapPin className="mr-2 h-4 w-4 text-gray-400" />
                      <span className="font-semibold">{po.city}, {po.state}</span>
                    </div>
                  </div>
                  <div className="space-y-1">
                    <p className="text-sm font-medium text-gray-500">Distributor</p>
                    <div className="flex items-center">
                      <Building className="mr-2 h-4 w-4 text-gray-400" />
                      <span className="font-semibold">{po.serving_distributor || 'Not assigned'}</span>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Summary Card */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <DollarSign className="mr-2 h-5 w-5" />
                  Order Summary
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Total Items</span>
                    <span className="font-semibold">{po.orderItems.length}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Total Quantity</span>
                    <span className="font-semibold">{totalQuantity}</span>
                  </div>
                  <Separator />
                  <div className="flex justify-between">
                    <span className="text-lg font-medium">Total Value</span>
                    <span className="text-lg font-bold text-primary">â‚¹{totalValue.toFixed(2)}</span>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Order Items */}
          <Card>
            <CardHeader>
              <CardTitle>Order Items ({po.orderItems.length})</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {po.orderItems.length === 0 ? (
                  <div className="text-center py-8">
                    <Package className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-500">No items in this purchase order</p>
                  </div>
                ) : (
                  <>
                    {/* Header */}
                    <div className="grid grid-cols-12 gap-4 p-3 bg-gray-50 dark:bg-gray-800 rounded-lg font-medium text-sm">
                      <div className="col-span-4">Item Name</div>
                      <div className="col-span-2 text-center">Quantity</div>
                      <div className="col-span-2 text-center">Landing Rate</div>
                      <div className="col-span-2 text-center">Total</div>
                      <div className="col-span-2 text-center">Status</div>
                    </div>

                    {/* Items */}
                    {po.orderItems.map((item) => (
                      <div key={item.id} className="grid grid-cols-12 gap-4 p-3 border border-gray-200 dark:border-gray-700 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors">
                        <div className="col-span-4">
                          <p className="font-medium">{item.item_name}</p>
                          {item.hsn_code && (
                            <p className="text-sm text-gray-500">HSN: {item.hsn_code}</p>
                          )}
                        </div>
                        <div className="col-span-2 text-center">
                          <span className="font-semibold">{item.quantity}</span>
                        </div>
                        <div className="col-span-2 text-center">
                          <span className="font-semibold">â‚¹{parseFloat(item.landing_rate).toFixed(2)}</span>
                        </div>
                        <div className="col-span-2 text-center">
                          <span className="font-semibold">â‚¹{(parseFloat(item.landing_rate) * item.quantity).toFixed(2)}</span>
                        </div>
                        <div className="col-span-2 text-center">
                          <Badge variant="outline" className="text-xs">
                            Active
                          </Badge>
                        </div>
                      </div>
                    ))}
                  </>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

client/src/pages/po-edit.tsx.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useParams, useLocation } from "wouter";
import { useState, useEffect } from "react";
import { format } from "date-fns";
import { ArrowLeft, Save, Trash2, Plus, X, Calendar } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { cn } from "@/lib/utils";
import type { PfPo, PfMst, PfOrderItems } from "@shared/schema";

interface POWithDetails extends Omit<PfPo, 'platform'> {
  platform: PfMst;
  orderItems: PfOrderItems[];
}

interface OrderItem {
  id?: number;
  item_name: string;
  quantity: number;
  landing_rate: string;
  hsn_code?: string;
}

export default function POEdit() {
  const params = useParams();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const poId = params.id;

  // Form state
  const [formData, setFormData] = useState({
    po_number: "",
    platform_id: "",
    status: "",
    order_date: "",
    expiry_date: "",
    city: "",
    state: "",
    serving_distributor: ""
  });
  const [orderItems, setOrderItems] = useState<OrderItem[]>([]);

  // Fetch PO data
  const { data: po, isLoading: poLoading } = useQuery<POWithDetails>({
    queryKey: [`/api/pos/${poId}`],
    enabled: !!poId
  });

  // Fetch platforms for dropdown
  const { data: platforms = [] } = useQuery<PfMst[]>({
    queryKey: ["/api/platforms"]
  });

  // Initialize form with PO data
  useEffect(() => {
    if (po) {
      setFormData({
        po_number: po.po_number,
        platform_id: po.platform.id.toString(),
        status: po.status || "",
        order_date: format(new Date(po.order_date), 'yyyy-MM-dd'),
        expiry_date: po.expiry_date ? format(new Date(po.expiry_date), 'yyyy-MM-dd') : "",
        city: po.city || "",
        state: po.state || "",
        serving_distributor: po.serving_distributor || ""
      });
      setOrderItems(po.orderItems.map(item => ({
        id: item.id,
        item_name: item.item_name,
        quantity: item.quantity,
        landing_rate: item.landing_rate,
        hsn_code: item.sap_code || ""
      })));
    }
  }, [po]);

  // Update PO mutation
  const updatePOMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest('PUT', `/api/pos/${poId}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/pos/${poId}`] });
      queryClient.invalidateQueries({ queryKey: ["/api/pos"] });
      toast({
        title: "Success",
        description: "Purchase order updated successfully"
      });
      setLocation(`/po-details/${poId}`);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update purchase order",
        variant: "destructive"
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.po_number || !formData.platform_id || !formData.order_date) {
      toast({
        title: "Validation Error",
        description: "Please fill in all required fields",
        variant: "destructive"
      });
      return;
    }

    if (orderItems.length === 0) {
      toast({
        title: "Validation Error",
        description: "Please add at least one order item",
        variant: "destructive"
      });
      return;
    }

    const submitData = {
      ...formData,
      platform_id: parseInt(formData.platform_id),
      expiry_date: formData.expiry_date || null,
      orderItems: orderItems.filter(item => item.item_name && item.quantity > 0)
    };

    updatePOMutation.mutate(submitData);
  };

  const addOrderItem = () => {
    setOrderItems([...orderItems, {
      item_name: "",
      quantity: 1,
      landing_rate: "0",
      hsn_code: ""
    }]);
  };

  const updateOrderItem = (index: number, field: keyof OrderItem, value: string | number) => {
    const updated = orderItems.map((item, i) => 
      i === index ? { ...item, [field]: value } : item
    );
    setOrderItems(updated);
  };

  const removeOrderItem = (index: number) => {
    setOrderItems(orderItems.filter((_, i) => i !== index));
  };

  const calculateTotal = () => {
    return orderItems.reduce((sum, item) => {
      return sum + (parseFloat(item.landing_rate) * item.quantity);
    }, 0);
  };

  if (poLoading) {
    return (
      <div className="flex-1 flex flex-col overflow-hidden">
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading purchase order...</p>
          </div>
        </div>
      </div>
    );
  }

  if (!po) {
    return (
      <div className="flex-1 flex flex-col overflow-hidden">
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-2">Purchase Order Not Found</h2>
            <p className="text-gray-600 mb-4">The requested purchase order could not be found.</p>
            <Button onClick={() => setLocation("/platform-po")}>
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back to Purchase Orders
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-1 flex flex-col overflow-hidden">
      {/* Header */}
      <header className="bg-gradient-to-r from-green-50 to-emerald-50 dark:from-gray-900 dark:to-gray-800 shadow-lg border-b border-green-100 dark:border-gray-700 px-6 py-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Button 
              variant="outline" 
              onClick={() => setLocation(`/po-details/${poId}`)}
              className="hover:bg-green-50"
            >
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back
            </Button>
            <div>
              <h1 className="text-3xl font-bold bg-gradient-to-r from-green-600 to-emerald-600 bg-clip-text text-transparent">
                Edit Purchase Order
              </h1>
              <p className="text-gray-600 dark:text-gray-300 mt-1">
                Modify details for {po.po_number}
              </p>
            </div>
          </div>
          <div className="flex items-center space-x-3">
            <Button 
              variant="outline" 
              onClick={() => setLocation(`/po-details/${poId}`)}
            >
              Cancel
            </Button>
            <Button 
              onClick={handleSubmit}
              disabled={updatePOMutation.isPending}
              className="bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700"
            >
              <Save className="mr-2 h-4 w-4" />
              {updatePOMutation.isPending ? "Saving..." : "Save Changes"}
            </Button>
          </div>
        </div>
      </header>

      {/* Content */}
      <main className="flex-1 overflow-y-auto p-6 bg-gray-50 dark:bg-gray-900">
        <div className="max-w-4xl mx-auto space-y-6">
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Basic Information */}
            <Card>
              <CardHeader>
                <CardTitle>Basic Information</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="po_number">PO Number *</Label>
                    <Input
                      id="po_number"
                      value={formData.po_number}
                      onChange={(e) => setFormData({...formData, po_number: e.target.value})}
                      placeholder="Enter PO number"
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="platform">Platform *</Label>
                    <Select 
                      value={formData.platform_id} 
                      onValueChange={(value) => setFormData({...formData, platform_id: value})}
                    >
                      <SelectTrigger>
                        <SelectValue placeholder="Select platform" />
                      </SelectTrigger>
                      <SelectContent>
                        {platforms.map((platform) => (
                          <SelectItem key={platform.id} value={platform.id.toString()}>
                            {platform.pf_name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="status">Status</Label>
                    <Select 
                      value={formData.status} 
                      onValueChange={(value) => setFormData({...formData, status: value})}
                    >
                      <SelectTrigger>
                        <SelectValue placeholder="Select status" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="open">Open</SelectItem>
                        <SelectItem value="closed">Closed</SelectItem>
                        <SelectItem value="cancelled">Cancelled</SelectItem>
                        <SelectItem value="expired">Expired</SelectItem>
                        <SelectItem value="duplicate">Duplicate</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="serving_distributor">Serving Distributor</Label>
                    <Input
                      id="serving_distributor"
                      value={formData.serving_distributor}
                      onChange={(e) => setFormData({...formData, serving_distributor: e.target.value})}
                      placeholder="Enter distributor name"
                    />
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Dates and Location */}
            <Card>
              <CardHeader>
                <CardTitle>Dates & Location</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="order_date">Order Date *</Label>
                    <Input
                      id="order_date"
                      type="date"
                      value={formData.order_date}
                      onChange={(e) => setFormData({...formData, order_date: e.target.value})}
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="expiry_date">Expiry Date</Label>
                    <Input
                      id="expiry_date"
                      type="date"
                      value={formData.expiry_date}
                      onChange={(e) => setFormData({...formData, expiry_date: e.target.value})}
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="city">City</Label>
                    <Input
                      id="city"
                      value={formData.city}
                      onChange={(e) => setFormData({...formData, city: e.target.value})}
                      placeholder="Enter city"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="state">State</Label>
                    <Input
                      id="state"
                      value={formData.state}
                      onChange={(e) => setFormData({...formData, state: e.target.value})}
                      placeholder="Enter state"
                    />
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Order Items */}
            <Card>
              <CardHeader className="flex flex-row items-center justify-between">
                <CardTitle>Order Items ({orderItems.length})</CardTitle>
                <Button type="button" onClick={addOrderItem} variant="outline">
                  <Plus className="mr-2 h-4 w-4" />
                  Add Item
                </Button>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {orderItems.length === 0 ? (
                    <div className="text-center py-8">
                      <p className="text-gray-500">No items added yet. Click "Add Item" to get started.</p>
                    </div>
                  ) : (
                    <>
                      {/* Header */}
                      <div className="grid grid-cols-12 gap-2 p-2 bg-gray-50 dark:bg-gray-800 rounded-lg font-medium text-sm">
                        <div className="col-span-4">Item Name</div>
                        <div className="col-span-2">Quantity</div>
                        <div className="col-span-2">Rate (â‚¹)</div>
                        <div className="col-span-2">HSN Code</div>
                        <div className="col-span-1">Total</div>
                        <div className="col-span-1">Action</div>
                      </div>

                      {/* Items */}
                      {orderItems.map((item, index) => (
                        <div key={index} className="grid grid-cols-12 gap-2 p-2 border border-gray-200 dark:border-gray-700 rounded-lg">
                          <div className="col-span-4">
                            <Input
                              value={item.item_name}
                              onChange={(e) => updateOrderItem(index, 'item_name', e.target.value)}
                              placeholder="Item name"
                              className="w-full"
                            />
                          </div>
                          <div className="col-span-2">
                            <Input
                              type="number"
                              min="1"
                              value={item.quantity}
                              onChange={(e) => updateOrderItem(index, 'quantity', parseInt(e.target.value) || 1)}
                              className="w-full"
                            />
                          </div>
                          <div className="col-span-2">
                            <Input
                              type="number"
                              step="0.01"
                              min="0"
                              value={item.landing_rate}
                              onChange={(e) => updateOrderItem(index, 'landing_rate', e.target.value)}
                              className="w-full"
                            />
                          </div>
                          <div className="col-span-2">
                            <Input
                              value={item.hsn_code}
                              onChange={(e) => updateOrderItem(index, 'hsn_code', e.target.value)}
                              placeholder="HSN code"
                              className="w-full"
                            />
                          </div>
                          <div className="col-span-1 flex items-center">
                            <span className="text-sm font-medium">
                              â‚¹{(parseFloat(item.landing_rate) * item.quantity).toFixed(2)}
                            </span>
                          </div>
                          <div className="col-span-1 flex items-center">
                            <Button
                              type="button"
                              variant="outline"
                              size="sm"
                              onClick={() => removeOrderItem(index)}
                              className="text-red-600 hover:text-red-700 hover:bg-red-50"
                            >
                              <X className="h-4 w-4" />
                            </Button>
                          </div>
                        </div>
                      ))}

                      {/* Total */}
                      <Separator />
                      <div className="flex justify-end">
                        <div className="text-right">
                          <p className="text-sm text-gray-600">Total Value</p>
                          <p className="text-xl font-bold text-primary">â‚¹{calculateTotal().toFixed(2)}</p>
                        </div>
                      </div>
                    </>
                  )}
                </div>
              </CardContent>
            </Card>
          </form>
        </div>
      </main>
    </div>
  );
}

client/src/components/po/order-items-lis.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { format, isAfter, isBefore, isEqual } from "date-fns";
import { Search, Filter, Download, RefreshCw, X, Calendar, Package, ChevronDown } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import * as XLSX from 'xlsx';
import type { PfMst, PfOrderItems } from "@shared/schema";

interface OrderItemWithDetails extends PfOrderItems {
  po_number: string;
  platform_name: string;
  order_date: Date;
  expiry_date: Date | null;
  platform: PfMst;
  hsn_code?: string | null;
}

export function OrderItemsListView() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState("");
  const [showFilter, setShowFilter] = useState(false);
  const [statusFilter, setStatusFilter] = useState("all");
  const [platformFilter, setPlatformFilter] = useState("all");
  const [orderDateFrom, setOrderDateFrom] = useState("");
  const [orderDateTo, setOrderDateTo] = useState("");
  const [expiryDateFrom, setExpiryDateFrom] = useState("");
  const [expiryDateTo, setExpiryDateTo] = useState("");

  const { data: orderItems = [], isLoading, refetch } = useQuery<OrderItemWithDetails[]>({
    queryKey: ["/api/order-items"]
  });

  const { data: platforms = [] } = useQuery<PfMst[]>({
    queryKey: ["/api/platforms"]
  });

  // Status update mutation
  const updateStatusMutation = useMutation({
    mutationFn: async ({ itemId, status }: { itemId: number, status: string }) => {
      const response = await fetch(`/api/order-items/${itemId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ status })
      });

      if (!response.ok) {
        throw new Error('Failed to update status');
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/order-items"] });
      toast({
        title: "Status Updated",
        description: "Order item status has been updated successfully"
      });
    },
    onError: (error) => {
      toast({
        title: "Update Failed",
        description: "Failed to update order item status",
        variant: "destructive"
      });
    }
  });

  const handleStatusUpdate = (itemId: number, newStatus: string) => {
    updateStatusMutation.mutate({ itemId, status: newStatus });
  };

  const handleRefresh = () => {
    refetch();
    toast({
      title: "Refreshed",
      description: "Order items list has been refreshed"
    });
  };

  const handleExport = () => {
    const exportData = filteredOrderItems.map(item => ({
      'PO Number': item.po_number,
      'Platform': item.platform_name,
      'Item Name': item.item_name,
      'SAP Code': item.sap_code || 'N/A',
      'HSN Code': item.hsn_code || 'N/A',
      'Quantity': item.quantity,
      'Basic Rate': parseFloat(item.basic_rate || '0'),
      'GST Rate': parseFloat(item.gst_rate || '0'),
      'Landing Rate': parseFloat(item.landing_rate || '0'),
      'Item Total': parseFloat((parseFloat(item.landing_rate || '0') * item.quantity).toFixed(2)),
      'Status': item.status || 'Pending',
      'Order Date': format(new Date(item.order_date), 'yyyy-MM-dd'),
      'Expiry Date': item.expiry_date ? format(new Date(item.expiry_date), 'yyyy-MM-dd') : 'Not set'
    }));

    const workbook = XLSX.utils.book_new();
    const worksheet = XLSX.utils.json_to_sheet(exportData);
    const colWidths = [
      { wch: 15 }, { wch: 20 }, { wch: 30 }, { wch: 15 }, { wch: 15 },
      { wch: 10 }, { wch: 12 }, { wch: 10 }, { wch: 12 }, { wch: 12 },
      { wch: 12 }, { wch: 12 }, { wch: 12 }
    ];
    worksheet['!cols'] = colWidths;
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Order Items');

    const filename = `order-items-${format(new Date(), 'yyyy-MM-dd')}.xlsx`;
    XLSX.writeFile(workbook, filename);

    toast({
      title: "Export Complete",
      description: `${filteredOrderItems.length} order items exported to Excel`
    });
  };

  const clearFilters = () => {
    setSearchTerm("");
    setStatusFilter("all");
    setPlatformFilter("all");
    setOrderDateFrom("");
    setOrderDateTo("");
    setExpiryDateFrom("");
    setExpiryDateTo("");
    setShowFilter(false);
  };

  // Filter order items based on search term and filters
  const filteredOrderItems = orderItems.filter(item => {
    const matchesSearch = searchTerm === "" || 
      item.item_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.po_number.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.platform_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (item.sap_code && item.sap_code.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (item.hsn_code && item.hsn_code.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesStatus = statusFilter === "all" || (item.status || 'pending').toLowerCase() === statusFilter.toLowerCase();
    const matchesPlatform = platformFilter === "all" || item.platform.id.toString() === platformFilter;

    // Date filters
    const itemOrderDate = new Date(item.order_date);
    const matchesOrderDateFrom = orderDateFrom === "" || isAfter(itemOrderDate, new Date(orderDateFrom)) || isEqual(itemOrderDate, new Date(orderDateFrom));
    const matchesOrderDateTo = orderDateTo === "" || isBefore(itemOrderDate, new Date(orderDateTo)) || isEqual(itemOrderDate, new Date(orderDateTo));

    let matchesExpiryDateFrom = true;
    let matchesExpiryDateTo = true;
    if (item.expiry_date) {
      const itemExpiryDate = new Date(item.expiry_date);
      matchesExpiryDateFrom = expiryDateFrom === "" || isAfter(itemExpiryDate, new Date(expiryDateFrom)) || isEqual(itemExpiryDate, new Date(expiryDateFrom));
      matchesExpiryDateTo = expiryDateTo === "" || isBefore(itemExpiryDate, new Date(expiryDateTo)) || isEqual(itemExpiryDate, new Date(expiryDateTo));
    } else {
      matchesExpiryDateFrom = expiryDateFrom === "";
      matchesExpiryDateTo = expiryDateTo === "";
    }

    return matchesSearch && matchesStatus && matchesPlatform && 
           matchesOrderDateFrom && matchesOrderDateTo && 
           matchesExpiryDateFrom && matchesExpiryDateTo;
  });

  // Calculate totals
  const totalItems = filteredOrderItems.length;
  const totalQuantity = filteredOrderItems.reduce((sum, item) => sum + item.quantity, 0);
  const totalValue = filteredOrderItems.reduce((sum, item) => 
    sum + (parseFloat(item.landing_rate || '0') * item.quantity), 0
  );

  const getStatusBadgeVariant = (status: string) => {
    switch (status.toLowerCase()) {
      case 'pending': return 'default';
      case 'invoiced': return 'secondary';
      case 'dispatched': return 'secondary';
      case 'delivered': return 'secondary';
      case 'cancelled': return 'destructive';
      case 'expired': return 'destructive';
      case 'price difference': return 'outline';
      case 'mov issue': return 'outline';
      case 'stock issue': return 'outline';
      case 'hold': return 'outline';
      case 'cn': return 'outline';
      case 'rtv': return 'outline';
      default: return 'default';
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-center">
          <div className="h-12 w-12 animate-spin rounded-full border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600 dark:text-gray-300">Loading order items...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Controls Bar */}
      <div className="space-y-4">
        <div className="flex items-center justify-between flex-wrap gap-4">
          <div className="flex items-center space-x-4">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
              <Input
                type="text"
                placeholder="Search order items..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 w-80 bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm border-gray-200 dark:border-gray-600"
              />
            </div>
            <Button 
              variant="outline" 
              onClick={() => setShowFilter(!showFilter)}
              className="flex items-center space-x-2"
            >
              <Filter className="h-4 w-4" />
              <span>Filter</span>
              {(statusFilter !== "all" || platformFilter !== "all" || 
                orderDateFrom !== "" || orderDateTo !== "" || 
                expiryDateFrom !== "" || expiryDateTo !== "") && (
                <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
              )}
            </Button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600 dark:text-gray-400">
              {filteredOrderItems.length} of {orderItems.length} items
            </span>
            <Button 
              variant="outline" 
              onClick={handleRefresh}
              disabled={isLoading}
              className="flex items-center space-x-2"
            >
              <RefreshCw className={`h-4 w-4 ${isLoading ? 'animate-spin' : ''}`} />
              <span>Refresh</span>
            </Button>
            <Button 
              variant="outline" 
              onClick={handleExport}
              disabled={filteredOrderItems.length === 0}
              className="flex items-center space-x-2"
            >
              <Download className="h-4 w-4" />
              <span>Export</span>
            </Button>
          </div>
        </div>

        {/* Filter Panel */}
        {showFilter && (
          <Card className="border border-gray-200 dark:border-gray-700">
            <CardContent className="p-4">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-sm font-medium text-gray-900 dark:text-white">Filters</h3>
                <div className="flex items-center space-x-2">
                  <Button variant="ghost" size="sm" onClick={clearFilters}>
                    Clear All
                  </Button>
                  <Button variant="ghost" size="sm" onClick={() => setShowFilter(false)}>
                    <X className="h-4 w-4" />
                  </Button>
                </div>
              </div>

              <div className="space-y-6">
                {/* Status and Platform Filters */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label className="text-sm font-medium text-gray-700 dark:text-gray-300">
                      Status
                    </Label>
                    <Select value={statusFilter} onValueChange={setStatusFilter}>
                      <SelectTrigger className="mt-2">
                        <SelectValue placeholder="All Status" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="all">All Status</SelectItem>
                        <SelectItem value="pending">Pending</SelectItem>
                        <SelectItem value="invoiced">Invoiced</SelectItem>
                        <SelectItem value="dispatched">Dispatched</SelectItem>
                        <SelectItem value="delivered">Delivered</SelectItem>
                        <SelectItem value="price difference">Price Difference</SelectItem>
                        <SelectItem value="mov issue">MOV Issue</SelectItem>
                        <SelectItem value="stock issue">Stock Issue</SelectItem>
                        <SelectItem value="cancelled">Cancelled</SelectItem>
                        <SelectItem value="expired">Expired</SelectItem>
                        <SelectItem value="hold">Hold</SelectItem>
                        <SelectItem value="cn">CN</SelectItem>
                        <SelectItem value="rtv">RTV</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <Label className="text-sm font-medium text-gray-700 dark:text-gray-300">
                      Platform
                    </Label>
                    <Select value={platformFilter} onValueChange={setPlatformFilter}>
                      <SelectTrigger className="mt-2">
                        <SelectValue placeholder="All Platforms" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="all">All Platforms</SelectItem>
                        {platforms.map((platform) => (
                          <SelectItem key={platform.id} value={platform.id.toString()}>
                            {platform.pf_name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                {/* Date Filters */}
                <div className="space-y-4">
                  <div className="flex items-center space-x-2">
                    <Calendar className="h-4 w-4 text-gray-500" />
                    <h4 className="text-sm font-medium text-gray-900 dark:text-white">Date Filters</h4>
                  </div>

                  {/* Order Date Range */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <Label className="text-sm font-medium text-gray-700 dark:text-gray-300">
                        Order Date From
                      </Label>
                      <Input
                        type="date"
                        value={orderDateFrom}
                        onChange={(e) => setOrderDateFrom(e.target.value)}
