            </CardHeader>
            <CardContent className="space-y-6">
              {parsedData && (
                <div className="space-y-6">
                  {/* Handle Multi-PO Display (Blinkit) */}
                  {parsedData.poList && Array.isArray(parsedData.poList) ? (
