                      <TableCell>{item.uom}</TableCell>
                      <TableCell>{item.unitsize}</TableCell>
                      <TableCell>{item.u_tax_rate}%</TableCell>
                      <TableCell>
                        <Badge variant={item.u_is_litre === "Y" ? "default" : "outline"}>
                          {item.u_is_litre === "Y" ? "Yes" : "No"}
