import { parse } from 'csv-parse/sync';
import * as XLSX from 'xlsx';

export interface BlinkitSecondarySalesItem {
  item_id: string;
  item_name: string;
  manufacturer_id: string;
  manufacturer_name: string;
  city_id: string;
  city_name: string;
  category: string;
  date: string;
  qty_sold: number;
  mrp: number;
}

export interface BlinkitSecondarySalesData {
  platform: "blinkit";
  businessUnit: string;
  periodType: "daily" | "date-range";
  reportDate?: string;
  periodStart?: string;
  periodEnd?: string;
  totalItems: number;
  items: BlinkitSecondarySalesItem[];
  summary?: {
    totalQtySold: number;
    totalSalesValue: number;
    uniqueProducts: number;
  };
}

export function parseBlinkitSecondarySalesFile(
  buffer: Buffer, 
  filename: string,
  businessUnit: string,
  periodType: "daily" | "date-range",
  reportDate?: string,
  periodStart?: string,
  periodEnd?: string
): BlinkitSecondarySalesData {
  let records: any[];

  try {
    if (filename.toLowerCase().endsWith('.csv')) {
      const content = buffer.toString('utf-8');
      records = parse(content, {
        columns: true,
        skip_empty_lines: true,
        trim: true
      });
    } else if (filename.toLowerCase().endsWith('.xlsx') || filename.toLowerCase().endsWith('.xls')) {
      const workbook = XLSX.read(buffer, { type: 'buffer' });
      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];
      records = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

      // Convert array format to object format with headers
      if (records.length > 0) {
        const headers = records[0] as string[];
        records = records.slice(1).map(row => {
          const obj: any = {};
          headers.forEach((header, index) => {
            obj[header] = (row as any[])[index];
          });
          return obj;
        });
      }
    } else {
      throw new Error('Unsupported file format. Please upload CSV or Excel files.');
    }

    console.log('Blinkit CSV Records Sample:', records.slice(0, 3));
    console.log('Expected headers: item_id, item_name, manufacturer_id, manufacturer_name, city_id, city_name, category, date, qty_sold, mrp');

    // Parse and validate items
    const items: BlinkitSecondarySalesItem[] = records
      .filter(record => record && typeof record === 'object')
      .map((record, index) => {
        try {
          // Handle different possible column names and formats
          const dateValue = String(record.date || record['Date'] || '').trim();
          const parsedDate = dateValue ? new Date(dateValue) : new Date();

          const item: BlinkitSecondarySalesItem = {
            item_id: String(record.item_id || record['Item ID'] || '').trim(),
            item_name: String(record.item_name || record['Item Name'] || '').trim(),
            manufacturer_id: String(record.manufacturer_id || record['Manufacturer ID'] || '').trim(),
            manufacturer_name: String(record.manufacturer_name || record['Manufacturer Name'] || '').trim(),
            city_id: String(record.city_id || record['City ID'] || '').trim(),
            city_name: String(record.city_name || record['City Name'] || '').trim(),
            category: String(record.category || record['Category'] || '').trim(),
            date: dateValue,
            qty_sold: parseFloat(record.qty_sold || record['Qty Sold'] || '0') || 0,
            mrp: parseFloat(record.mrp || record['MRP'] || '0') || 0
          };

          // Validate required fields
          if (!item.item_id || !item.item_name) {
            console.warn(`Row ${index + 1}: Missing required fields (item_id: ${item.item_id}, item_name: ${item.item_name})`);
            return null;
          }

          return item;
        } catch (error) {
          console.error(`Error parsing row ${index + 1}:`, error);
          return null;
        }
      })
      .filter((item): item is BlinkitSecondarySalesItem => item !== null);

    console.log(`Successfully parsed ${items.length} Blinkit items`);

    // Calculate summary
    // Note: In Blinkit CSV, the 'mrp' field contains the total selling price (qty * unit_price), not unit price
    const summary = {
      totalQtySold: items.reduce((sum, item) => sum + item.qty_sold, 0),
      totalSalesValue: items.reduce((sum, item) => sum + item.mrp, 0), // MRP is already total value
      uniqueProducts: new Set(items.map(item => item.item_id)).size
    };

    const result: BlinkitSecondarySalesData = {
      platform: "blinkit",
      businessUnit,
      periodType,
      totalItems: items.length,
      items,
      summary
    };

    if (periodType === "daily" && reportDate) {
      result.reportDate = reportDate;
    } else if (periodType === "date-range" && periodStart && periodEnd) {
      result.periodStart = periodStart;
      result.periodEnd = periodEnd;
    }

    return result;

  } catch (error) {
    console.error('Error parsing Blinkit secondary sales file:', error);
    throw new Error(`Failed to parse Blinkit secondary sales file: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

server/zepto-secondary-sales-parser.ts.content:
import { parse } from 'csv-parse';
import { InsertZeptoSecondarySalesItem } from '@shared/schema';

export interface ZeptoSecondaryParseResult {
  success: boolean;
  data?: InsertZeptoSecondarySalesItem[];
  error?: string;
  totalItems?: number;
}

export function parseZeptoSecondaryData(csvContent: string, reportDate: Date, periodStart?: Date, periodEnd?: Date): Promise<ZeptoSecondaryParseResult> {
  return new Promise((resolve) => {
    const results: InsertZeptoSecondarySalesItem[] = [];
    let hasError = false;
    let errorMessage = '';

    const parser = parse({
      columns: true,
      skip_empty_lines: true,
      trim: true
    });

    parser.on('readable', function() {
      let record;
      while ((record = parser.read()) !== null) {
        try {
          // Parse date from CSV (expected format: DD-MM-YYYY)
          const dateStr = record['Date'];
          let parsedDate: Date = new Date();

          if (dateStr && typeof dateStr === 'string') {
            if (dateStr.includes('-')) {
              const parts = dateStr.split('-');
              if (parts.length === 3) {
                const day = parseInt(parts[0]);
                const month = parseInt(parts[1]);
                const year = parseInt(parts[2]);
                if (!isNaN(day) && !isNaN(month) && !isNaN(year)) {
                  parsedDate = new Date(year, month - 1, day);
                }
              }
            }

            // Fallback: try parsing directly
            if (isNaN(parsedDate.getTime())) {
              parsedDate = new Date(dateStr);
            }

            // Another fallback: try ISO format
            if (isNaN(parsedDate.getTime())) {
              parsedDate = new Date(dateStr + 'T00:00:00.000Z');
            }
          }

          // Validate date - if still invalid, use current date
          if (isNaN(parsedDate.getTime())) {
            console.warn(`Invalid date found: ${dateStr}, using current date`);
            parsedDate = new Date();
          }

          const item: InsertZeptoSecondarySalesItem = {
            report_date: reportDate,
            date: parsedDate,
            sku_number: record['SKU Number'] || null,
            sku_name: record['SKU Name'] || null,
            ean: record['EAN'] || null,
            sku_category: record['SKU Category'] || null,
            sku_sub_category: record['SKU Sub Category'] || null,
            brand_name: record['Brand Name'] || null,
            manufacturer_name: record['Manufacturer Name'] || null,
            manufacturer_id: record['Manufacturer ID'] || null,
            city: record['City'] || null,
            sales_qty_units: record['Sales (Qty) - Units'] ? parseInt(record['Sales (Qty) - Units']) : null,
            mrp: record['MRP'] ? String(parseFloat(record['MRP'])) : null,
            gmv: record['Gross Merchandise Value'] ? String(parseFloat(record['Gross Merchandise Value'])) : null,
            attachment_path: null // Will be set by calling function
          };

          // Add period information for range reports
          if (periodStart && periodEnd) {
            (item as any).period_start = periodStart;
            (item as any).period_end = periodEnd;
          }

          results.push(item);
        } catch (error) {
          console.error('Error parsing Zepto secondary sales row:', error);
          hasError = true;
          errorMessage = `Failed to parse row: ${error instanceof Error ? error.message : 'Unknown error'}`;
        }
      }
    });

    parser.on('error', function(err) {
      console.error('CSV parsing error:', err);
      hasError = true;
      errorMessage = `CSV parsing failed: ${err.message}`;
    });

    parser.on('end', function() {
      if (hasError && results.length === 0) {
        resolve({
          success: false,
          error: errorMessage
        });
      } else {
        resolve({
          success: true,
          data: results,
          totalItems: results.length
        });
      }
    });

    parser.write(csvContent);
    parser.end();
  });
}

