import * as csv from 'csv-parse';

export interface BlinkitInventoryRow {
  sku_id: string;
  product_name: string;
  category: string;
  subcategory: string;
  brand: string;
  size: string;
  unit: string;
  stock_on_hand: string;
  reserved_quantity: string;
  available_quantity: string;
  inbound_quantity: string;
  outbound_quantity: string;
  damaged_quantity: string;
  expired_quantity: string;
  last_updated_at: string;
  warehouse_location: string;
  supplier_name: string;
}

export interface ParsedBlinkitInventoryData {
  platform: string;
  businessUnit: string;
  periodType: 'daily' | 'range';
  reportDate?: string;
  periodStart?: string;
  periodEnd?: string;
  totalItems: number;
  items: BlinkitInventoryRow[];
  summary: {
    totalProducts: number;
    totalStockOnHand: number;
    totalAvailableQuantity: number;
    totalReservedQuantity: number;
    totalDamagedQuantity: number;
    totalExpiredQuantity: number;
  };
}

export async function parseBlinkitInventoryCsv(
  csvContent: string,
  businessUnit: string,
  periodType: 'daily' | 'range',
  reportDate?: Date,
  periodStart?: Date | null,
  periodEnd?: Date | null
): Promise<ParsedBlinkitInventoryData> {

  return new Promise((resolve, reject) => {
    const items: BlinkitInventoryRow[] = [];

    csv.parse(csvContent, {
      columns: true,
      skip_empty_lines: true,
      delimiter: ',',
      quote: '"',
      escape: '"',
      trim: true
    }, (err, records) => {
      if (err) {
        console.error('CSV parsing error:', err);
        reject(new Error(`Failed to parse CSV: ${err.message}`));
        return;
      }

      try {
        if (!records || records.length === 0) {
          reject(new Error('No data found in CSV file'));
          return;
        }

        console.log(`Processing ${records.length} records from Blinkit inventory CSV`);
        console.log('First record keys:', Object.keys(records[0] || {}));

        // Process each record
        for (const record of records) {
          // Cast record to any to handle dynamic property access
          const recordData = record as any;

          // Debug: Log first few records to understand structure
          if (items.length < 3) {
            console.log(`Record ${items.length + 1}:`, recordData);
          }

          // Skip empty rows - check multiple possible field names
          const hasSkuId = recordData.sku_id || recordData.SKU_ID || recordData['SKU ID'] || recordData.item_id || recordData.Item_ID;
          const hasProductName = recordData.product_name || recordData.Product_Name || recordData['Product Name'] || recordData.item_name || recordData.Item_Name;

          if (!hasSkuId && !hasProductName) {
            console.log('Skipping empty row:', recordData);
            continue;
          }

          const item: BlinkitInventoryRow = {
            sku_id: recordData.sku_id || recordData.SKU_ID || recordData['SKU ID'] || recordData.item_id || recordData.Item_ID || '',
            product_name: recordData.product_name || recordData.Product_Name || recordData['Product Name'] || recordData.item_name || recordData.Item_Name || '',
            category: recordData.category || recordData.Category || '',
            subcategory: recordData.subcategory || recordData.Subcategory || recordData['Sub Category'] || '',
            brand: recordData.brand || recordData.Brand || recordData.manufacturer_name || recordData.Manufacturer_Name || '',
            size: recordData.size || recordData.Size || '',
            unit: recordData.unit || recordData.Unit || '',
            stock_on_hand: recordData.stock_on_hand || recordData.Stock_On_Hand || recordData['Stock on Hand'] || recordData.qty_sold || '0',
            reserved_quantity: recordData.reserved_quantity || recordData.Reserved_Quantity || recordData['Reserved Quantity'] || '0',
            available_quantity: recordData.available_quantity || recordData.Available_Quantity || recordData['Available Quantity'] || '0',
            inbound_quantity: recordData.inbound_quantity || recordData.Inbound_Quantity || recordData['Inbound Quantity'] || '0',
            outbound_quantity: recordData.outbound_quantity || recordData.Outbound_Quantity || recordData['Outbound Quantity'] || '0',
            damaged_quantity: recordData.damaged_quantity || recordData.Damaged_Quantity || recordData['Damaged Quantity'] || '0',
            expired_quantity: recordData.expired_quantity || recordData.Expired_Quantity || recordData['Expired Quantity'] || '0',
            last_updated_at: recordData.last_updated_at || recordData.Last_Updated_At || recordData['Last Updated'] || recordData.date || '',
            warehouse_location: recordData.warehouse_location || recordData.Warehouse_Location || recordData['Warehouse Location'] || recordData.city_name || recordData.City_Name || '',
            supplier_name: recordData.supplier_name || recordData.Supplier_Name || recordData['Supplier Name'] || recordData.manufacturer_name || recordData.Manufacturer_Name || ''
          };

          items.push(item);
        }

        if (items.length === 0) {
          console.log('No valid items found. Total records processed:', records.length);
          reject(new Error('No valid inventory records found in CSV. Please ensure your CSV has columns like sku_id, product_name, stock_on_hand, etc.'));
          return;
        }

        console.log(`Successfully processed ${items.length} Blinkit inventory items`);

        // Calculate summary statistics
        const totalStockOnHand = items.reduce((sum, item) => sum + (parseInt(item.stock_on_hand) || 0), 0);
        const totalAvailableQuantity = items.reduce((sum, item) => sum + (parseInt(item.available_quantity) || 0), 0);
        const totalReservedQuantity = items.reduce((sum, item) => sum + (parseInt(item.reserved_quantity) || 0), 0);
        const totalDamagedQuantity = items.reduce((sum, item) => sum + (parseInt(item.damaged_quantity) || 0), 0);
        const totalExpiredQuantity = items.reduce((sum, item) => sum + (parseInt(item.expired_quantity) || 0), 0);

        const result: ParsedBlinkitInventoryData = {
          platform: 'blinkit',
          businessUnit,
          periodType,
          reportDate: reportDate?.toISOString(),
          periodStart: periodStart?.toISOString(),
          periodEnd: periodEnd?.toISOString(),
          totalItems: items.length,
          items,
          summary: {
            totalProducts: items.length,
            totalStockOnHand,
            totalAvailableQuantity,
            totalReservedQuantity,
            totalDamagedQuantity,
            totalExpiredQuantity
          }
        };

        console.log(`Successfully parsed ${items.length} Blinkit inventory records`);
        resolve(result);

      } catch (error) {
        console.error('Error processing Blinkit inventory CSV data:', error);
        reject(new Error(`Failed to process inventory data: ${error instanceof Error ? error.message : 'Unknown error'}`));
      }
    });
  });
}

server/flipkart-parser.ts.content:
import * as XLSX from 'xlsx';

export interface FlipkartSecondarySalesData {
  tenantId: string;
  retailerName: string;
  retailerCode: string;
  fsn: string;
  productName: string;
  category: string;
  subCategory: string;
  brand: string;
  mrp: number;
  sellingPrice: number;
  salesData: Array<{
    date: string;
    qty: number;
  }>;
  totalSalesQty: number;
  totalSalesValue: number;
}

export interface ParsedFlipkartSecondarySalesData {
  platform: string;
  businessUnit: string;
  periodType: string;
  reportDate?: string;
  periodStart?: string;
  periodEnd?: string;
  totalItems: number;
  totalValue: number;
  uniqueProducts: number;
  data: FlipkartSecondarySalesData[];
}

export function parseFlipkartSecondaryData(buffer: Buffer, periodType: string, businessUnit: string, startDate?: string, endDate?: string): ParsedFlipkartSecondarySalesData {
  try {
    const workbook = XLSX.read(buffer, { type: 'buffer' });
    const sheetName = workbook.SheetNames[0];
    const sheet = workbook.Sheets[sheetName];

    // Get all cell data as JSON
    const jsonData = XLSX.utils.sheet_to_json(sheet, { header: 1 });

    if (!jsonData || jsonData.length < 2) {
      throw new Error('Invalid file format: No data rows found');
    }

    const headers = jsonData[0] as string[];
    const dataRows = jsonData.slice(1) as any[][];

    // Find column indices for static fields based on actual Flipkart headers
    const tenantIdIndex = headers.findIndex(h => h && h.toLowerCase().includes('tenant'));
    const retailerIdIndex = headers.findIndex(h => h && h.toLowerCase().includes('retailer id'));
    const retailerNameIndex = headers.findIndex(h => h && h.toLowerCase().includes('retailer name'));
    const fsnIndex = headers.findIndex(h => h && h.toLowerCase().includes('fsn'));
    const productNameIndex = headers.findIndex(h => h && h.toLowerCase().includes('product title')); // Changed from 'product name'
    const categoryIndex = headers.findIndex(h => h && h.toLowerCase().includes('category') && !h.toLowerCase().includes('sub'));
    const verticalIndex = headers.findIndex(h => h && h.toLowerCase().includes('vertical'));
    const brandIndex = headers.findIndex(h => h && h.toLowerCase().includes('brand'));
    const eanIndex = headers.findIndex(h => h && h.toLowerCase().includes('ean'));
    const hsnIndex = headers.findIndex(h => h && h.toLowerCase().includes('hsn'));
    const styleCodeIndex = headers.findIndex(h => h && h.toLowerCase().includes('style code'));
    const lastCalculatedAtIndex = headers.findIndex(h => h && h.toLowerCase().includes('lastcalculatedat'));

    // Find date columns (Flipkart uses YYYY-MM-DD format starting from index 7)
    const dateColumns: Array<{ index: number; date: string }> = [];
    const dateRegex = /^\d{4}-\d{2}-\d{2}$/;

    headers.forEach((header, index) => {
      if (header && dateRegex.test(header.toString())) {
        dateColumns.push({
          index,
          date: header.toString()
        });
      }
    });

    console.log(`Found ${dateColumns.length} date columns in Flipkart file`);

    if (dateColumns.length === 0) {
      throw new Error('No date columns found in the file');
    }

    // Calculate date range based on period type
    let calculatedStartDate = startDate;
    let calculatedEndDate = endDate;

    if (periodType === '2-month') {
      const today = new Date();
      const dayOfYear = Math.floor((today.getTime() - new Date(today.getFullYear(), 0, 0).getTime()) / (1000 * 60 * 60 * 24));

      const start = new Date(today);
      start.setMonth(start.getMonth() - 2);
      start.setDate(start.getDate() + (dayOfYear % 30));

      const end = new Date(today);
      end.setDate(end.getDate() + (dayOfYear % 30));

      calculatedStartDate = start.toISOString().split('T')[0];
      calculatedEndDate = end.toISOString().split('T')[0];
    }

    const parsedData: FlipkartSecondarySalesData[] = [];

    for (const row of dataRows) {
      if (!row || row.length === 0) continue;

      // Extract basic product information based on actual Flipkart file structure
      const tenantId = row[tenantIdIndex]?.toString() || '';
      const retailerId = row[retailerIdIndex]?.toString() || '';
      const retailerName = row[retailerNameIndex]?.toString() || '';
      const fsn = row[fsnIndex]?.toString() || '';
      const productName = row[productNameIndex]?.toString() || ''; // This will now get "Product Title"
      const category = row[categoryIndex]?.toString() || '';
      const vertical = row[verticalIndex]?.toString() || '';
      const brand = row[brandIndex]?.toString() || '';
      const ean = row[eanIndex]?.toString() || '';
      const hsn = row[hsnIndex]?.toString() || '';
      const styleCode = row[styleCodeIndex]?.toString() || '';

      // Flipkart doesn't have MRP and selling price in their file structure
      const mrp = 0; // Not available in Flipkart files
      const sellingPrice = 0; // Not available in Flipkart files

      // Extract sales data from date columns
      const salesData: Array<{ date: string; qty: number }> = [];
      let totalSalesQty = 0;

      for (const dateCol of dateColumns) {
        const qty = parseInt(row[dateCol.index]?.toString() || '0') || 0;
        if (qty > 0) {
          salesData.push({
            date: dateCol.date,
            qty
          });
          totalSalesQty += qty;
        }
      }

      const totalSalesValue = totalSalesQty * sellingPrice;

      if (totalSalesQty > 0) { // Only include rows with sales
        parsedData.push({
          tenantId,
          retailerName,
          retailerCode: retailerId, // Using retailerId as retailerCode
          fsn,
          productName,
          category,
          subCategory: vertical, // Using vertical as subCategory
          brand,
          mrp,
          sellingPrice,
          salesData,
          totalSalesQty,
          totalSalesValue
        });
      }
    }

    const totalValue = parsedData.reduce((sum, item) => sum + item.totalSalesValue, 0);
    const uniqueProducts = new Set(parsedData.map(item => item.fsn)).size;

    return {
      platform: 'flipkart-grocery',
      businessUnit,
      periodType,
      reportDate: periodType === 'daily' ? calculatedStartDate : undefined,
      periodStart: periodType !== 'daily' ? calculatedStartDate : undefined,
      periodEnd: periodType !== 'daily' ? calculatedEndDate : undefined,
      totalItems: parsedData.length,
      totalValue,
      uniqueProducts,
      data: parsedData
    };

  } catch (error) {
    console.error('Error parsing Flipkart secondary sales data:', error);
    throw new Error(`Failed to parse Flipkart file: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

server/objectAcl.ts.content:
import { File } from "@google-cloud/storage";

const ACL_POLICY_METADATA_KEY = "custom:aclPolicy";

// The type of the access group.
//
// Can be flexibly defined according to the use case.
//
// Examples:
// - USER_LIST: the users from a list stored in the database;
// - EMAIL_DOMAIN: the users whose email is in a specific domain;
// - GROUP_MEMBER: the users who are members of a specific group;
// - SUBSCRIBER: the users who are subscribers of a specific service / content
//   creator.
export enum ObjectAccessGroupType {}

// The logic user group that can access the object.
export interface ObjectAccessGroup {
  // The type of the access group.
  type: ObjectAccessGroupType;
  // The logic id that is enough to identify the qualified group members.
  //
  // It may have different format for different types. For example:
  // - for USER_LIST, the id could be the user list db entity id, and the
  //   user list db entity could contain a bunch of user ids. User needs
  //   to be a member of the user list to be able to access the object.
  // - for EMAIL_DOMAIN, the id could be the email domain, and the user needs
  //   to have an email with the domain to be able to access the object.
  // - for GROUP_MEMBER, the id could be the group db entity id, and the
  //   group db entity could contain a bunch of user ids. User needs to be
  //   a member of the group to be able to access the object.
  // - for SUBSCRIBER, the id could be the subscriber db entity id, and the
  //   subscriber db entity could contain a bunch of user ids. User needs to
  //   be a subscriber to be able to access the object.
  id: string;
}

export enum ObjectPermission {
  READ = "read",
  WRITE = "write",
}

export interface ObjectAclRule {
  group: ObjectAccessGroup;
  permission: ObjectPermission;
}

// The ACL policy of the object.
// This would be set as part of the object custom metadata:
// - key: "custom:aclPolicy"
// - value: JSON string of the ObjectAclPolicy object.
export interface ObjectAclPolicy {
  owner: string;
  visibility: "public" | "private";
  aclRules?: Array<ObjectAclRule>;
}

// Check if the requested permission is allowed based on the granted permission.
function isPermissionAllowed(
  requested: ObjectPermission,
  granted: ObjectPermission,
): boolean {
  // Users granted with read or write permissions can read the object.
  if (requested === ObjectPermission.READ) {
    return [ObjectPermission.READ, ObjectPermission.WRITE].includes(granted);
  }

  // Only users granted with write permissions can write the object.
  return granted === ObjectPermission.WRITE;
}

// The base class for all access groups.
//
// Different types of access groups can be implemented according to the use case.
abstract class BaseObjectAccessGroup implements ObjectAccessGroup {
  constructor(
    public readonly type: ObjectAccessGroupType,
    public readonly id: string,
  ) {}

  // Check if the user is a member of the group.
  public abstract hasMember(userId: string): Promise<boolean>;
}

function createObjectAccessGroup(
  group: ObjectAccessGroup,
): BaseObjectAccessGroup {
  switch (group.type) {
    // Implement the case for each type of access group to instantiate.
    //
    // For example:
    // case "USER_LIST":
    //   return new UserListAccessGroup(group.id);
    // case "EMAIL_DOMAIN":
    //   return new EmailDomainAccessGroup(group.id);
    // case "GROUP_MEMBER":
    //   return new GroupMemberAccessGroup(group.id);
    // case "SUBSCRIBER":
    //   return new SubscriberAccessGroup(group.id);
    default:
      throw new Error(`Unknown access group type: ${group.type}`);
  }
}

// Sets the ACL policy to the object metadata.
export async function setObjectAclPolicy(
  objectFile: File,
  aclPolicy: ObjectAclPolicy,
): Promise<void> {
  const [exists] = await objectFile.exists();
  if (!exists) {
    throw new Error(`Object not found: ${objectFile.name}`);
  }

  await objectFile.setMetadata({
    metadata: {
      [ACL_POLICY_METADATA_KEY]: JSON.stringify(aclPolicy),
    },
  });
}

// Gets the ACL policy from the object metadata.
export async function getObjectAclPolicy(
  objectFile: File,
): Promise<ObjectAclPolicy | null> {
  const [metadata] = await objectFile.getMetadata();
  const aclPolicy = metadata?.metadata?.[ACL_POLICY_METADATA_KEY];
  if (!aclPolicy) {
    return null;
  }
  return JSON.parse(aclPolicy as string);
}

// Checks if the user can access the object.
export async function canAccessObject({
  userId,
  objectFile,
  requestedPermission,
}: {
  userId?: string;
  objectFile: File;
  requestedPermission: ObjectPermission;
}): Promise<boolean> {
  // When this function is called, the acl policy is required.
  const aclPolicy = await getObjectAclPolicy(objectFile);
  if (!aclPolicy) {
    return false;
  }

  // Public objects are always accessible for read.
  if (
    aclPolicy.visibility === "public" &&
    requestedPermission === ObjectPermission.READ
  ) {
    return true;
  }

  // Access control requires the user id.
  if (!userId) {
    return false;
  }

  // The owner of the object can always access it.
  if (aclPolicy.owner === userId) {
    return true;
  }

  // Go through the ACL rules to check if the user has the required permission.
  for (const rule of aclPolicy.aclRules || []) {
    const accessGroup = createObjectAccessGroup(rule.group);
    if (
      (await accessGroup.hasMember(userId)) &&
      isPermissionAllowed(requestedPermission, rule.permission)
    ) {
      return true;
    }
  }

  return false;
}

